and #{tjq.startTime} < msg_time
and msg_time < #{tjq.endTime}
and #{tjq.startWeight} < total_weight
and total_weight < #{tjq.endWeight}
and #{tjq.chesuStart} < speed
and speed < #{tjq.chesuEnd}
and #{tjq.roadTmpStart} < Temperature
and Temperature < #{tjq.roadTmpEnd}
and #{tjq.chaozhongStart} < Over_Weight_Ratio
and Over_Weight_Ratio < #{tjq.chaozhongEnd}
